var PromiseClass = require('./');
var App = PromiseClass.create({
    aaa(n){
        return n;
    }
});
var app = new App();
var App2 = PromiseClass.create({
});
var app2 = new App2();
App2.addMethod('aaa', function(done){
    var args = Array.prototype.slice.call(arguments, 0);
    var callback = args.pop();
    app.aaa.apply(app, args).then(function(ret){
        callback(null, ret)
    }, callback);
});
app2.aaa(123, function(error, ret){
    console.log(ret)
    return 321;
}).then(function(ret){
    console.log(ret)
})
